mapper.select:
SELECT a.replyNum, a.postNum, a.email, b.nickname, a.content, a.postTime, a.accusedYN, a.rereplyCnt
FROM reply AS a JOIN student AS b ON a.email = b.email
WHERE a.postNum = #{postNum}

INSERT INTO rereply (replyNum,email,content,postTime,accusedYN) VALUES(#{replyNum},#{email},#{content},NOW(),0);
UPDATE reply SET rereplyCnt = rereplyCnt + 1 WHERE replyNum = #{replyNum};
UPDATE post SET replyNum = replyNum + 1 WHERE postNum = #{postNum};

SELECT a.rereplyNum, a.replyNum, a.email, b.nickname, a.content, a.postTime, a.accusedYN
FROM rereply AS a JOIN student AS b ON a.email = b.email
WHERE a.replyNum = #{replyNum}
